import React, { useState } from 'react';
import './style.css';


function App() {
  // Fechas de disponibilidad para el alquiler
  const fechas = [
    { fecha: '2024-11-01', disponible: true },
    { fecha: '2024-11-02', disponible: true },
    { fecha: '2024-11-03', disponible: false },
    { fecha: '2024-11-04', disponible: true },
  ];

  const [disponibilidad, setDisponibilidad] = useState(fechas);

  const handleClick = (fechaSeleccionada) => {
    // Aquí puedes agregar lógica para bloquear fechas ya seleccionadas
    alert(`Seleccionaste: ${fechaSeleccionada}`);
  };

  return (
    <div className="alquiler">
      <img src="imagen-alquiler.jpg" alt="Imagen del alquiler" />
      <h2>Alquiler 1</h2>
      <p>Precio: $100/noche</p>
      <div className="fechas">
        {disponibilidad.map((item) => (
          <div
            key={item.fecha}
            className={`fecha ${item.disponible ? 'disponible' : 'no-disponible'}`}
            data-fecha={item.fecha}
            onClick={() => handleClick(item.fecha)}
          >
            {item.fecha.slice(8, 10)} {/* Extrae solo el día del formato yyyy-mm-dd */}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
